import './styles/App.css';

import {
  BrowserRouter,
  createBrowserRouter,
  Navigate,
  Route,
  RouterProvider,
  Routes,
} from 'react-router-dom';

/** import components */
import Main from './components/Main';
import Quiz from './components/Quiz';
import Result from './components/Result';
import { CheckUserExist } from './helper/helper';
import Dashboard from './pages/Dashboard';
import Signup from './pages/Signup';

import { UseAuthContext } from './hooks/useAuthContext';

/** react routes */
const router = createBrowserRouter([
  {
    path: '/',
    element: user ? <Dashboard /> : <Navigate to="/Signup" />,
  },
  {
    path: '/quiz-home',
    element: <Main />,
  },
  {
    path: '/quiz',
    element: (
      <CheckUserExist>
        <Quiz />
      </CheckUserExist>
    ),
  },
  {
    path: '/result',
    element: (
      <CheckUserExist>
        <Result />
      </CheckUserExist>
    ),
  },
  {
    path: '/Signup',
    element: <Signup />,
  },
]);

function App() {
  console.log(user);
  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element={
            <Dashboard
              Hero={Hero}
              NewArrivals={NewArrivals}
              Collections={Collections}
            />
          }
        />
        <Route
          path="/login-or-signup"
          element={!user ? <ParentLogin /> : <Navigate to="/" />}
        />

        <Route
          path="/wishlist"
          element={<Wishlist wishlist={wishlist} Products={Products} />}
        />
        <Route
          path="/your-bag"
          element={<Bag cartItems={cartItems} Products={Products} />}
        />

        <Route
          path="/:categorypath"
          element={<Categories Products={Products} />}
        />
        <Route
          path="/:productparent/:product"
          element={<ProductPage Products={Products} />}
        />

        <Route path="nr-kids/:nextpage" element={<Nextpage />} />

        <Route
          path="/my-profile/:id"
          element={<ProfileBody userData={userData} />}
        />

        <Route
          path="/your-bag/check-out"
          element={<Checkout Products={Products} />}
        />

        <Route path="/check-out" element={<Buynow Products={Products} />} />

        <Route path="/phone-verify" element={<MobileVerify />} />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
}

export default App;
