  const [isLoading, setIsLoading] = useState(null);
  const { dispatch } = UseAuthContext();

  const signup = async (_id, fullName, email, password, role) => {
    setIsLoading(true);
    setError(null);

    
  };
  return { signup, isLoading, signerror };
};
